import os

from .client import Client  # noqa
from .exceptions import (BadRequestsError, ForbiddenError,  # noqa
                         GatewayTimeoutError, HTTPError, InternalServerError,
                         MethodNotAllowedError, NotFoundError,
                         PayloadTooLargeError, ServiceUnavailableError,
                         TooManyRequestsError, UnauthorizedError,
                         UnsupportedMediaTypeError)

dir_path = os.path.dirname(os.path.realpath(__file__))
if os.path.isfile(os.path.join(dir_path, 'VERSION.txt')):
    with open(os.path.join(dir_path, 'VERSION.txt')) as version_file:
        __version__ = version_file.read().strip()
